 function toggleMobileMenu(){document.querySelector('body').classList.toggle('mobile-nav-active');const overlay=document.querySelector('.mobile-nav-overlay');if(overlay){overlay.classList.toggle('active');}const mobileNavToggle=document.querySelector('.mobile-nav-toggle');if(mobileNavToggle){mobileNavToggle.classList.toggle('bi-list');mobileNavToggle.classList.toggle('bi-x');}}document.addEventListener('DOMContentLoaded',function(){const mobileMenuBtn=document.querySelector('.mobile-nav-toggle');if(mobileMenuBtn){mobileMenuBtn.addEventListener('click',function(e){e.preventDefault();toggleMobileMenu();});}const overlay=document.querySelector('.mobile-nav-overlay');if(overlay){overlay.addEventListener('click',function(){if(document.querySelector('body').classList.contains('mobile-nav-active')){toggleMobileMenu();}});}const closeBtn=document.querySelector('.mobile-menu-close');if(closeBtn){closeBtn.addEventListener('click',function(){if(document.querySelector('body').classList.contains('mobile-nav-active')){toggleMobileMenu();}});}const menuItems=document.querySelectorAll('#navmenu a');menuItems.forEach(item=>{item.addEventListener('click',function(){if(document.querySelector('body').classList.contains('mobile-nav-active')){toggleMobileMenu();}});});});